def get_discounts():
    #check if max debt is reached
    bondClosed = maxDebtReached(bond_address='0x765C7CFED02f2d9583EAc8229930f3650AF42C77')
    if bondClosed is True:
        return -999
    else:
        #get prices from CoinGecko
        principalPrice = cg.get_price(ids='curve-dao-token', vs_currencies='usd')['curve-dao-token']['usd']
        try:
            ohmPrice = get_price(lp_address='0x055475920a8c93CfFb64d039A8205F7AcC7722d3')
            btrflyPrice = get_price(lp_address='0xe9AB8038Ee6Dd4fCC7612997FE28d4e22019C4B4', basePrice=ohmPrice)
        except:
            btrflyPrice = cg.get_price(ids='butterflydao', vs_currencies='usd')['butterflydao']['usd']

        tokenDeposit = 1e9 * principalPrice/btrflyPrice
        
        depositValue = treasury_value(treasury_address='0x086C98855dF3C78C6b481b6e1D47BeF42E9aC36B', token_address='0xD533a949740bb3306d119CC777fa900bA034cd52')
        payout = bond_discount(bond_address='0x765C7CFED02f2d9583EAc8229930f3650AF42C77', depositValue=depositValue)
        return ((payout / tokenDeposit) - 1)


@client.event
async def on_ready():
    print('Logged in as {0.user}'.format(client))
    if not check_discounts.is_running():
        check_discounts.start()


@tasks.loop(seconds = 90)
async def check_discounts():
    bondDisc = get_discounts()
    
    for guild in client.guilds:
        guser = guild.get_member(client.user.id)
        try:
            if bondDisc == -999:
                await guser.edit(nick=f'Sold Out!')
                await client.change_presence(activity=discord.Activity(type=discord.ActivityType.playing, name=f'â›” CRV Bonds'))
            elif bondDisc < 0:
                await guser.edit(nick=f'{-100*bondDisc:,.2f}% Premium')
                await client.change_presence(activity=discord.Activity(type=discord.ActivityType.playing, name=f'CRV Bonds'))
            else:
                await guser.edit(nick=f'{100*bondDisc:,.2f}% Discount')
                await client.change_presence(activity=discord.Activity(type=discord.ActivityType.playing, name=f'CRV Bonds'))

        except Exception as e:
            print("check_discounts error")
            print(e)


client.run(BOT_TOKEN)